SELECT title FROM movies, stars, people WHERE movies.id = stars.movie_id AND stars.person_id = people.id AND movies.id IN (SELECT movies.id FROM movies, stars, people WHERE movies.id = stars.movie_id AND stars.person_id = people.id AND stars.person_id = (SELECT id FROM people WHERE name = "Johnny Depp")) AND stars.person_id = (SELECT id FROM people WHERE name = "Helena Bonham Carter");